mapper.insert:
insert into event (id, priority, title, 
  desc, tags, topic, status,
  create_time, update_time, complete_time
  )
values (#{id,jdbcType=INTEGER}, #{priority,jdbcType=TINYINT}, #{title,jdbcType=VARCHAR}, 
  #{desc,jdbcType=VARCHAR}, #{tags,jdbcType=VARCHAR}, #{topic,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT},
  #{createTime,jdbcType=BIGINT}, #{updateTime,jdbcType=BIGINT}, #{completeTime,jdbcType=BIGINT}
  )

mapper.select:
select id, priority, title, desc, tags, topic, status, create_time, update_time, complete_time
from event
where id = #{id,jdbcType=INTEGER}
